lue="update" />							<input type="hidden" name="id" value="0" />														<div class="form-group">								<label class="col-lg-2 control-label">Leading Sektor</label>								<div class="col-lg-10">									<input type="text" name="leading_sektor" class="form-control" placeholder="Leading Sektor" />								</div>							</div>							<div class="form-group">								<label class="col-lg-2 control-label">No Surat</label>								<div class="col-lg-10">									<input type="text" name="no_surat" class="form-control" placeholder="No Surat" />								</div>							</div>							<div class="form-group">								<label class="col-lg-2 control-label">Tempat</label>								<div class="col-lg-10">									<input type="text" name="tempat" class="form-control" placeholder="Tempat" />								</div>							</div>							<div class="form-group">								<label class="col-lg-2 control-label">Tanggal Ajuan</label>								<div class="col-lg-10">									<div class="input-append datepicker">										<input name="tanggal_ajuan_date" type="text" class="form-control dtpicker" placeholder="Tanggal Ajuan" data-format="dd-MM-yyyy" />										<span class="add-on"><i data-time-icon="fa fa-time" data-date-icon="fa fa-calendar" class="btn btn-info"></i></span>									</div>								</div>							</div>							<div class="form-group">								<label class="col-lg-2 control-label">Waktu Ajuan</label>								<div class="col-lg-10">									<div class="input-append timepicker">										<input name="tanggal_ajuan_time" type="text" class="form-control tmpicker" placeholder="Waktu Ajuan" data-format="hh:mm:ss" />										<span class="add-on"><i data-time-icon="fa fa-clock-o" data-date-icon="fa fa-calendar" class="btn btn-info"></i></span>									</div>								</div>							</div>							<div class="form-group">								<label class="col-lg-2 control-label">Tanggal Undangan</label>								<div class="col-lg-10">									<div class="input-append datepicker">										<input name="tanggal_undangan" type="text" class="form-control dtpicker" placeholder="Tanggal Undangan" data-format="dd-MM-yyyy" />										<span class="add-on"><i data-time-icon="fa fa-time" data-date-icon="fa fa-calendar" class="btn btn-info"></i></span>									</div>								</div>							</div>							<div class="form-group">								<label class="col-lg-2 control-label">Pimpinan Rapat</label>								<div class="col-lg-10">									<input type="text" name="pimpinan_rapat" class="form-control" placeholder="Pimpinan Rapat" />								</div>							</div>							<div class="form-group">								<label class="col-lg-2 control-label">Pakaian</label>								<div class="col-lg-10">									<input type="text" name="pakaian" class="form-control" placeholder="Pakaian" />								</div>							</div>							<div class="form-group">								<label class="col-lg-2 control-label">Catatan</label>								<div class="col-lg-10">									<input type="text" name="catatan" class="form-control" placeholder="Catatan" />								</div>							</div>							<div class="form-group">								<label class="col-lg-2 control-label">Keterangan</label>								<div class="col-lg-10">									<input type="text" name="keterangan" class="form-control" placeholder="Keterangan" />								</div>							</div>														<hr />							<div class="form-group">								<div class="col-lg-offset-2 col-lg-9">									<button type="submit" class="btn btn-info">Save</button>									<button type="button" class="btn btn-info btn-show-grid">Cancel</button>								</div>							</div>						</form></div>					</div>				</div>  							</div></div>        </div></div>    </div>	<div class="clearfix"></div></div><?php $this->load->view( 'common/footer' ); ?><?php $this->load->view( 'common/library_js'); ?><script>$(document).ready(function() {	var dt = null;	var page = {		show_grid: function() {			$('.grid-main').show();			$('#form-surat').hide();		},		show_form: function() {			$('.grid-main').hide();			$('#form-surat').show();		}	}		// global	$('.btn-show-grid').click(function() {		page.show_grid();	});		// grid	var param = {		id: 'datatable',		source: web.host + 'surat/agenda_rapat/home/grid',		column: [ { }, { }, { }, { }, { bSortable: false, sClass: "center" } ],		callback: function() {			$('#datatable .btn-edit').click(function() {				var raw_record = $(this).siblings('.hide').text();				eval('var record = ' + raw_record);								Func.ajax({ url: web.host + 'surat/agenda_rapat/home/action', param: { action: 'get_by_id', id: record.id }, callback: function(result) {					Func.populate({ cnt: '#form-surat', record: result });					$('#form-surat form').valid();					page.show_form();				} });			});						$('#datatable .btn-delete').click(function() {				var raw_record = $(this).siblings('.hide').text();				eval('var record = ' + raw_record);								Func.form.del({					data: { action: 'delete', id: record.id },					url: web.host + 'surat/agenda_rapat/home/action', callback: function() { dt.reload(); }				});			});		}	}	dt = Func.datatable(param);		// form surat	$('.btn-add').click(function() {		page.show_form();		$('#form-surat form')[0].reset();		$('#form-surat [name="id"]').val(0);	});	$('#form-surat form').validate({		rules: {			leading_sektor: { required: true },			no_surat: { required: true },			tempat: { required: true },			tanggal_ajuan_date: { required: true },			tanggal_ajuan_time: { required: true }		}	});	$('#form-surat form').submit(function(e) {		e.preventDefault();		if (! $('#form-surat form').valid()) {			return false;		}				var param = Func.form.get_value('form-surat');		param.tanggal_ajuan = param.tanggal_ajuan_date + ' ' + param.tanggal_ajuan_time;		Func.form.submit({			url: web.host + 'surat/agenda_rapat/home/action',			param: param,			callback: function(result) {				dt.reload();				page.show_grid();				$('#form-surat form')[0].reset();			}		});	});});</script></body></html>